import React, { useState } from "react";
import { OpenAI } from "openai";

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true,
});

const TextToSpeechDashboard = () => {
  const [inputText, setInputText] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!inputText) return alert("Please enter some text.");

    setIsProcessing(true);

    try {
      // Directly call the OpenAI API
      const mp3 = await openai.audio.speech.create({
        model: "tts-1",
        voice: "alloy",
        input: inputText,
      });

      console.log("passed");

      const buffer = Buffer.from(await mp3.arrayBuffer());
      const blob = new Blob([buffer], { type: "audio/mpeg" });
      const url = URL.createObjectURL(blob);

      // Play the audio
      const audio = new Audio(url);
      audio.play();
    } catch (error) {
      console.error(error);
      alert("Error generating text-to-speech. Please try again.");
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto bg-white shadow rounded">
      <h2 className="text-xl font-bold mb-4">Text-to-Speech Dashboard</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          className="w-full border p-2 rounded mb-4"
          rows="4"
          placeholder="Enter text to convert to speech"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button
          type="submit"
          className={`w-full bg-blue-500 text-white py-2 px-4 rounded ${
            isProcessing ? "opacity-50 cursor-not-allowed" : ""
          }`}
          disabled={isProcessing}
        >
          {isProcessing ? "Processing..." : "Convert to Speech"}
        </button>
      </form>
    </div>
  );
};

export default TextToSpeechDashboard;
