import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { useAuth0 } from "@auth0/auth0-react";
import { io } from "socket.io-client";

const socket = io("http://localhost:3001"); // Connect to the backend

function Dashboard() {
  const { user, isAuthenticated } = useAuth0();
  const [sessionId, setSessionId] = useState("");

  useEffect(() => {
    // Fetch the session ID from the backend
    const fetchSessionId = async () => {
      try {
        const uniqueId = user.sub.split("|")[1];
        setSessionId(uniqueId);

        socket.emit("register", uniqueId);
      } catch (error) {
        console.error("Error fetching session ID:", error);
      }
    };
    fetchSessionId();
  }, [user]);

  if (!isAuthenticated) {
    return <p>Please log in to access your dashboard.</p>;
  }

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "50vh",
        backgroundColor: "#f8f9fa",
      }}
    >
      <Card style={{ width: "1000px", padding: "20px", borderRadius: "12px" }}>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            marginBottom: "20px",
          }}
        >
          <img
            src={user.picture}
            alt="User"
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
              marginRight: "20px",
            }}
          />
          <div>
            <h2 style={{ margin: 0 }}>{user.name}</h2>
            <p style={{ color: "gray", fontSize: "14px" }}>
              Call Invite ID: {sessionId}
            </p>
          </div>
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            marginTop: "20px",
          }}
        >
          <div style={{ textAlign: "center", flex: 1, marginRight: "10px" }}>
            <h5>Start Call</h5>
            <Button variant="primary" style={{ width: "100%" }}>
              Start
            </Button>
          </div>
          <div style={{ textAlign: "center", flex: 1, marginLeft: "10px" }}>
            <h5>Start Learning</h5>
            <Button variant="success" style={{ width: "100%" }}>
              Learn
            </Button>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default Dashboard;
