import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import LoginButton from "./LoginButton";
import LogoutButton from "./LogoutButton";
import { useAuth0 } from "@auth0/auth0-react";
import Profile from "./Profile"; // Import the Profile component

function LightNavbar() {
  const { user, isAuthenticated } = useAuth0();
  const [showProfile, setShowProfile] = useState(false); // State to toggle profile view

  // Function to handle profile navigation
  const handleProfileClick = () => {
    setShowProfile(true);
  };

  return showProfile ? (
    <Profile /> // Render the Profile component when the profile is clicked
  ) : (
    <Navbar bg="primary" data-bs-theme="dark">
      <Container>
        <Navbar.Brand href="#home">Visual Voice</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link href="#features">Learn</Nav.Link>
          <Nav.Link href="#pricing">Tutors</Nav.Link>
        </Nav>
        <Nav className="align-items-center">
          {!isAuthenticated ? (
            <LoginButton />
          ) : (
            <>
              <LogoutButton />
              <img
                src={user.picture}
                alt="Profile"
                className="rounded-circle"
                style={{
                  width: "40px",
                  height: "40px",
                  cursor: "pointer",
                  marginLeft: "15px",
                }}
                onClick={handleProfileClick}
              />
            </>
          )}
        </Nav>
      </Container>
    </Navbar>
  );
}

export default LightNavbar;
