import React, { useState } from "react";
import * as PlayHT from "playht";

PlayHT.init({
  userId: process.env.REACT_APP_PLAY_HT_USER_ID,
  apiKey: process.env.REACT_APP_PLAY_HT_API_KEY,
  defaultVoiceId:
    "s3://peregrine-voices/oliver_narrative2_parrot_saad/manifest.json",
  defaultVoiceEngine: "PlayDialog",
});

async function playGeneratedAudio(text) {
  try {
    const generated = await PlayHT.generate(text);
    const { audioUrl } = generated;
    const audio = new Audio(audioUrl);
    audio.play();
  } catch (error) {
    console.error("Error generating audio:", error);
  }
}

const TextToSpeech = () => {
  const [text, setText] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim()) {
      playGeneratedAudio(text);
    } else {
      alert("Please enter some text.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
      <h1>Text to Speech</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text here"
          style={{
            width: "100%",
            padding: "10px",
            marginBottom: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#007BFF",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Generate Audio
        </button>
      </form>
    </div>
  );
};

export default TextToSpeech;
